import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tags: [],
            search: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }
    componentDidMount() {
        fetch('/api/tags/')
            .then(data => data.json())
            .then((logs) => this.setState({ "logs": logs }))
    };

    async handleChange(e) {
        let val = e.target.value;
        this.setState({ "search": val });
    }

    render() {
        return (
            <input style={{ border: "none" }} type="text" class="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />

        )
    }
}

export default App;
