import React, { Component } from 'react';
import { Switch, Route } from 'react-router-dom';
import Problems from './Problems';
import Search from './Search';
import './App.css';
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: new Set()
        }
        this.addTag = this.addTag.bind(this)
        this.removeTag = this.removeTag.bind(this)
    }

    addTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.add(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    removeTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.delete(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    render() {
        return (
            <div className="App">
                <Switch >
                    <Route path="/" exact render={props => <Search selectedTags={this.state.selectedTags} addTag={this.addTag} removeTag={this.removeTag} />} />
                    {

                        <Route path="/tags/problems/" exact render={props => <Problems selectedTags={Array.from(this.state.selectedTags)} />} />}
                </Switch>
            </div>
        )
    }
}

export default App;
