import React, { Component } from 'react';
import { Switch, Route } from 'react-router-dom';
import Problems from './Problems';
import Search from './Search';
import './App.css';
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: new Set()
        }
        this.addTag = this.addTag.bind(this)
        this.removeTag = this.removeTag.bind(this)
        this.getProblems = this.getProblems.bind(this)
    }

    addTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.add(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    removeTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.delete(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }
    async getProblems() {
        let list = ""
        Array.from(this.state.selectedTags).map(tag => (
            list += tag.id + ","
        ))
        list = list.slice(0, -1);
        console.log(list)
        await fetch('/api/tags/')
            .then(data => data.json())
            .then((res) => {
                console.log(res);
                this.setState({ "problems": res })
            })
    }

    render() {
        return (
            <div className="App">
                <Switch >
                    <Route path="/" exact render={props => <Search selectedTags={this.state.selectedTags} addTag={this.addTag} removeTag={this.removeTag} />} />
                    <Route path="/tags/problems/" exact render={props => <Problems problems = {this.state.problems} getProblems = {this.getProblems} selectedTags={Array.from(this.state.selectedTags)} />} />
                </Switch>
            </div>
        )
    }
}

export default App;
