import React, { Component } from 'react';
import { Switch, Route, Link } from 'react-router-dom';
import Problems from './Problems';
import Search from './Search';
import './App.css';
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: new Set(),
            searchResult: [],
            search: ""
        }
        this.addTag = this.addTag.bind(this)
        this.re
    }


    addTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.add(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    removeTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.delete(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    render() {
        return (
            <div className="App">
                {
                    Array.from(this.state.selectedTags).map((item, index) => (
                        <li key={item.id}>{item.tag_name} <span onClick={(e) => this.removeTag(item)}> x </span></li>
                    ))
                }
                <br></br>
                <div>
                    <input style={{ border: "none" }} type="text" className="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />
                    <span> <Link to="/tags/problems/" replace> Search </Link> </span>
                </div>
                <div>
                    {
                        this.state.searchResult.map(tag => (
                            <p key={tag.id} onClick={(e) => this.addTag(tag)}> {tag.tag_name} </p>
                        ))
                    }
                </div>
                <Switch >
                    <Route path="/" exact render={props => <Search selectedTags={Array.from(this.state.selectedTags)} />} />
                    <Route path="/tags/problems/" exact render={props => <Problems selectedTags={Array.from(this.state.selectedTags)} />} />
                </Switch>
            </div>
        )
    }
}

export default App;
