import React, { Component } from 'react';
import { Dropdown } from 'react-bootstrap';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: [],
            searchResult: [],
            search: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(e) {
        let val = e.target.value;
        this.setState({ "search": val });

    }

    render() {
        return (
            <div className="App">
                <input style={{ border: "none" }} type="text" class="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />
                {this.state.tags}
                <div className="dropdown">
                    <Dropdown>
                        <Dropdown.Toggle
                            variant="secondary btn-sm"
                            id="dropdown-basic">
                            Language
        </Dropdown.Toggle>

                        <Dropdown.Menu style={{ backgroundColor: '#73a47' }}>
                            <Dropdown.Item href="#" >Arabic</Dropdown.Item>
                            <Dropdown.Item href="#">English</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </div>
            </div>
        )
    }
}

export default App;
