import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags : [], 
            search: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(e) {
        let val = e.target.value;
        this.setState({ "search": val });
    }

    render() {
        return (
            <div>
                <input style={{ border: "none" }} type="text" class="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />
                {this.state.tags}
            </div>
        )
    }
}

export default App;
