import React, { Component } from 'react';
import { Switch, Route } from 'react-router-dom';
import './App.css';
import Problems from './Problems';
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: new Set(),
            searchResult: [],
            search: ""
        }
        this.addTag = this.addTag.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    async handleChange(e) {
        let val = e.target.value;
        this.setState({ "search": val });
        if (val.length === 0)
            this.setState({ "searchResult": [] });
        else {
            await fetch('api/tags/search/' + val)
                .then(data => data.json())
                .then((res) => this.setState({ "searchResult": res }))
        }
    }

    addTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.add(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    removeTag(tag) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.delete(tag);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    render() {
        return (
            <div className="App">
                {
                    Array.from(this.state.selectedTags).map((item, index) => (
                        <li key={item.id}>{item.tag_name} <span onClick={(e) => this.removeTag(item)}> x </span></li>
                    ))
                }
                <br></br>
                <div>
                    <input style={{ border: "none" }} type="text" className="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />
                    <span> Search </span>
                </div>
                <div>
                    {
                        this.state.searchResult.map(tag => (
                            <p key={tag.id} onClick={(e) => this.addTag(tag)}> {tag.tag_name} </p>
                        ))
                    }
                </div>
                <Switch >
                    <Route path="/tags/problems/" exact render={props => <Problems selectedTags = {Array.from()}/>} />
                </Switch>
            </div>
        )
    }
}

export default App;
