import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: new Set(),
            searchResult: [],
            search: ""
        }
        this.addTag = this.addTag.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    async handleChange(e) {
        let val = e.target.value;
        this.setState({ "search": val });
        if(val.length === 0) 
            this.setState({"searchResult" : []});
        else  {
            await fetch('api/tags/search/' + val)
                .then(data => data.json())
                .then((res) => this.setState({ "searchResult": res }))
        }
    }

    addTag(id) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.add({"id" : id, });
        this.setState({
            "selectedTags" : newSelectedTags
        });
    }

    removeTag(id) {
        let newSelectedTags = this.state.selectedTags;
        newSelectedTags.delete(id);
        this.setState({
            "selectedTags": newSelectedTags
        });
    }

    render() {
        return (
            <div className="App">
                {
                    Array.from(this.state.selectedTags).map((item, index) => (
                        <li key={item.id}>{item.tag_name} <span onClick={(e) => this.removeTag(item.id)}> x </span></li> 
                    ))
                }
                <br></br>
                <input style={{ border: "none" }} type="text" className="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />
                <div>
                    {
                        this.state.searchResult.map(tag => (
                            <p key={tag.id} onClick={(e) => this.addTag(tag)}> {tag.tag_name} </p>
                        ))
                    }
                </div>
            </div>
        )
    }
}

export default App;
