import React, { Component } from 'react';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTags: [],
            searchResult: [],
            search: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    async handleChange(e) {
        let val = e.target.value;
        this.setState({ "search": val });
        await fetch('api/tags/search/' + val)
            .then(data => data.json())
            .then((res) => this.setState({ "searchResult": res }))
    }

    render() {
        return (
            <div className="App">
                <input style={{ border: "none" }} type="text" class="form-control" value={this.state.search} onChange={(e) => this.handleChange(e)} placeholder="Search" />
                {
                    this.state.searchResult.map()
                }
            </div> 
        )
    }
}

export default App;
