import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
class Tags extends Component {
    constructor(props) {
        super(props);
        this.state = {
            "tags": [],
            "redirect": false
        }
    }
    componentDidMount() {
        this.props.removeAll()
        fetch('/api/tags/')
            .then(data => data.json())
            .then((res) => {
                console.log(res);
                this.setState({ "tags": res })
            })

    }

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/tags' />
        }
    }

    render() {
        return (
            <div>
                {
                    this.state.tags.map(item => (
                        <li id = {item.id} onClick = {e => {this.props.addTag(item)}}> {item.tag_name} </li>
                    ))
                }
            </div>
        )
    }
}
export default Tags;
